<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta http-equiv="X-UA-Compatible" content="IE=edge" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <link rel="preconnect" href="https://fonts.googleapis.com" />
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin />
    <link
      href="https://fonts.googleapis.com/css2?family=Roboto:wght@100;300;400;500;700;900&display=swap"
      rel="stylesheet"
    />
    <link rel="stylesheet" href="style.css" />
    <script
      src="https://kit.fontawesome.com/bd12abc9fe.js"
      crossorigin="anonymous"
    ></script>
    <title>Giga Logowanie</title>
  </head>
  <body>
    <!-- Login/Join form -->
    <div class="form_container" id="form_container">
      <form action="index.html" method="POST">
        <span>Zaloguj się</span><br />
        <span>Nazwa użytkownika</span>
        <input type="text" placeholder="Login" required />
        <span>Hasło</span>
        <input type="password" placeholder="Hasło" required />
        <input type="submit" class="submit" placeholder="Zaloguj" />
      </form>
    </div>

    <div class="form_container_reg" id="form_container_reg">
      <form action="index.html" method="POST">
        <span>Zarejestruj się</span><br />
        <span>Nazwa użytkownika</span>
        <input type="text" placeholder="Login" required />
        <span>Hasło</span>
        <input type="password" placeholder="Hasło" required />
        <input type="submit" class="submit" placeholder="Zaloguj" />
      </form>
    </div>
    <!-- End Of Form -->


    <!-- End Of Content Section -->

    <!-- Footer -->
    <footer class="footer" id="footer">
      <span>Wykonał: Michał Ziółkowski, Bartłomiej Kamaszewski, Damian Janus</span>
      <a href="https://www.wsb.pl/warszawa/">Wyższa Szkoła Bankowa 2022</a>
    </footer>
    <!-- End Of Footer -->

    <!-- JS SRC -->
    <script src="app.js"></script>
    <!-- JS SRC END -->
  </body>
</html>
